frappe.ui.form.on("Sales Order Item", "bag", function(frm, cdt, cdn) {
	// code for calculate total and set on parent field.
	grand_total = 0;
	$.each(frm.doc.items || [], function(i, d) {
		grand_total += flt(d.bag);
	});
	frm.set_value("total_bag", grand_total);
});

